const t=(t,e="yyyy-mm-dd")=>{t||(t=Number(new Date)),10==t.toString().length&&(t*=1e3);const n=new Date(t);let g;const o={"y+":n.getFullYear().toString(),"m+":(n.getMonth()+1).toString(),"d+":n.getDate().toString(),"h+":n.getHours().toString(),"M+":n.getMinutes().toString(),"s+":n.getSeconds().toString()};for(const r in o)g=new RegExp(`(${r})`).exec(e),g&&(e=e.replace(g[1],1==g[1].length?o[r]:o[r].padStart(g[1].length,"0")));return e};export{t};
